namespace TrackTime

[<AutoOpen>]
module Icons =
    open Avalonia.FuncUI.DSL
    open Avalonia.Controls
    open Avalonia.FuncUI.Builder
    open Avalonia.FuncUI.Types
    open Avalonia.Media

    type Image with
        static member source<'t when 't :> Image>(value: IImage) : IAttr<'t> =
            // TODO: maybe add custom bitmap comparer OR pass enum that has different options ?
            AttrBuilder<'t>
                .CreateProperty<IImage>(Image.SourceProperty, value, ValueNone)

    let private createDrawingImage brush geometryData =
        let geometryDrawing = new GeometryDrawing()
        geometryDrawing.Brush <- Brush.Parse(brush)
        geometryDrawing.Geometry <- Geometry.Parse(geometryData)

        Image.create [ Image.source (new DrawingImage(geometryDrawing))
                       Image.classes [ "buttonIcon" ] ]

